        addElements()
    }

    func addElements(){
        view.addSubview(eulaTitle)
        eulaTitle.topAnchor.constraint(equalTo: view.topAnchor, constant: 50).isActive = true
        eulaTitle.centerXAnchor.constraint(equalTo: view.centerXAnchor).isActive = true
        eulaTitle.widthAnchor.constraint(equalTo: view.widthAnchor).isActive = true
        eulaTitle.heightAnchor.constraint(equalToConstant: 35).isActive = true

        textSheet.delegate = self
        view.addSubview(textSheet)
        textSheet.topAnchor.constraint(equalTo: eulaTitle.bottomAnchor, constant: 10).isActive = true
        textSheet.centerXAnchor.constraint(equalTo: view.centerXAnchor).isActive = true
        textSheet.heightAnchor.constraint(equalTo: view.heightAnchor, constant: -200).isActive = true
        textSheet.widthAnchor.constraint(equalToConstant: screenSizes.width).isActive = true
        
        view.addSubview(acceptBtn)
        acceptBtn.topAnchor.constraint(equalTo: textSheet.bottomAnchor, constant: 15).isActive = true
        acceptBtn.centerXAnchor.constraint(equalTo: view.centerXAnchor).isActive = true
        acceptBtn.heightAnchor.constraint(equalToConstant: 35).isActive = true
        acceptBtn.widthAnchor.constraint(equalToConstant: 150).isActive = true
        
        self.view.bringSubviewToFront(eulaTitle)
    }
    
    func scrollViewDidScroll(_ scrollView: UIScrollView) {
        acceptBtn.isHidden = scrollView.contentOffset.y + scrollView.bounds.height < scrollView.contentSize.height
    }
    
    @objc func backToDating(){
